import React from 'react';
import { Carousel, Row, Col, Button } from 'antd';
import { Link } from 'react-router-dom';

const Welcome = () => {
  const images = [
    { id: 1, text: 'Hoşgeldiniz', image: 'https://via.placeholder.com/150' },
    { id: 2, text: 'Ev ekleyebilirsiniz', image: 'https://via.placeholder.com/150' },
    { id: 3, text: 'Evleri düzenleyebilirsiniz', image: 'https://via.placeholder.com/150' },
  ];

  return (
    <div style={{ textAlign: 'center', padding: '50px' }}>
      <Carousel autoplay autoplaySpeed={7000}>
        {images.map((img) => (
          <div key={img.id}>
            <div style={{
              height: '300px',
              backgroundImage: `url(${img.image})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              color: 'white',
              fontSize: '24px'
            }}>
              {img.text}
            </div>
          </div>
        ))}
      </Carousel>
      <div style={{ marginTop: '20px' }}>
        <Row gutter={[16, 16]} justify="center">
          <Col span={8}>
            <Link to="/property-input">
              <Button type="primary">
                Ev Ekle
              </Button>
            </Link>
          </Col>
          <Col span={8}>
            <Link to="/property-edit">
              <Button type="primary">
                Düzenle
              </Button>
            </Link>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default Welcome;
