import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Upload, Select, message } from 'antd';
import axios from 'axios';
import { saveAs } from 'file-saver';
import * as XLSX from 'xlsx';
const { Option } = Select;

const PropertyInputPage = () => {
  const [fileList, setFileList] = useState([]);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [selectedCityId, setSelectedCityId] = useState(null);
  const [loadingDistricts, setLoadingDistricts] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await axios.get(
          'https://v1.nocodeapi.com/ebr123/google_sheets/MBXXeYjRrYchwfWk?tabId=ıller&filterValue=SEHİRLER'
        );
        setCities(response.data.data);
      } catch (error) {
        console.error('İller alınırken hata oluştu:', error);
      }
    };
    fetchCities();
  }, []);

  useEffect(() => {
    const fetchDistricts = async () => {
      if (!selectedCityId) return;
      setLoadingDistricts(true);
      try {
        const response = await axios.get(
          `https://v1.nocodeapi.com/ebr123/google_sheets/MBXXeYjRrYchwfWk?tabId=ılceler&filterValue=ılceler`
        );
        const filteredDistricts = response.data.data.filter(item => item.id === selectedCityId);
        setDistricts(filteredDistricts.map(item => item.ılceler));
      } catch (error) {
        console.error('İlçeler alınırken hata oluştu:', error);
      } finally {
        setLoadingDistricts(false);
      }
    };
    fetchDistricts();
  }, [selectedCityId]);

  const handleUpload = ({ fileList }) => {
    setFileList(fileList);
  };

  const handleCityChange = (value) => {
    setSelectedCityId(value);
  };
  const handleCategoryChange = (value) => {
    setSelectedCategory(value);
  };
  const handleTypeChange = (value) => {
    setSelectedType(value);
  };

  const handleSubmit = async (values) => {
    try {
      const propertyData = {
        ilanbaslik: values.name,
        ilanaciklama: values.description,
        fiyat: values.price,
        il: selectedCityId,
        ilce: values.district,
        kategori: selectedCategory,
        tur: selectedType,
      };

      // Mevcut Excel dosyasını okuma veya yeni bir tane oluşturma
      const fileResponse = await fetchExistingExcelFile();
      let wb;
      let ws;
      const wsName = 'EmlakVerileri';

      if (fileResponse) {
        wb = XLSX.read(fileResponse, { type: 'array' });
        ws = wb.Sheets[wsName];
      } else {
        wb = XLSX.utils.book_new();
        ws = XLSX.utils.json_to_sheet([], { header: ['ilanbaslik', 'ilanaciklama', 'fiyat', 'il', 'ilce', 'kategori', 'tur'] });
        XLSX.utils.book_append_sheet(wb, ws, wsName);
      }

      // Yeni veriyi ekle
      XLSX.utils.sheet_add_json(ws, [propertyData], { skipHeader: true, origin: -1 });

      // Dosyayı güncelle ve kaydet
      const wbout = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
      saveAs(new Blob([wbout], { type: 'application/octet-stream' }), 'EmlakVerileri.xlsx');

      message.success('Veri başarıyla kaydedildi');
    } catch (error) {
      console.error('Veri kaydedilirken hata oluştu:', error);
      message.error('Veri kaydedilirken bir hata oluştu');
    }
  };

  const fetchExistingExcelFile = async () => {
    try {
      const response = await axios.get('http://localhost:3000/EmlakVerileri.xlsx', {
        responseType: 'arraybuffer',
      });
      return response.data;
    } catch (error) {
      console.error('Mevcut Excel dosyası alınırken hata oluştu:', error);
      return null;
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto' }}>
      <h1>Emlak Bilgilerini Girin</h1>
      <Form layout="vertical" onFinish={handleSubmit}>
        <Form.Item label="İlan Başlığı" name="name" rules={[{ required: true, message: 'Lütfen ilan başlığını girin.' }]}>
          <Input />
        </Form.Item>
        <Form.Item label="İlan Açıklaması" name="description">
          <Input.TextArea />
        </Form.Item>
        <Form.Item label="Fiyat" name="price" rules={[{ required: true, message: 'Lütfen fiyatı girin.' }]}>
          <Input />
        </Form.Item>
        <p>İller:</p>
        <Select style={{ width: '100%' }} placeholder="İl seçin" onChange={handleCityChange}>
          {cities.map((city, index) => (
            <Option key={city.id} value={city.id}>
              {city.SEHİRLER}
            </Option>
          ))}
        </Select>
        <p>İlçeler:</p>
        <Select style={{ width: '100%' }} placeholder="İlçe seçin" loading={loadingDistricts} disabled={!selectedCityId}>
          {districts.map((district, index) => (
            <Option key={index} value={district}>
              {district}
            </Option>
          ))}
        </Select>
        <Form.Item label="Kategori" name="category" rules={[{ required: true, message: 'Lütfen kategoriyi seçin.' }]}>
          <Select onChange={handleCategoryChange}>
            <Option value="konut">Konut</Option>
            <Option value="ticari">Ticari</Option>
            <Option value="arsa">Arsa</Option>
            <Option value="diğer">Diğer</Option>
          </Select>
        </Form.Item>
        <Form.Item label="Tür" name="type" rules={[{ required: true, message: 'Lütfen türü seçin.' }]}>
          <Select onChange={handleTypeChange}>
            <Option value="kiralik">Kiralık</Option>
            <Option value="satilik">Satılık</Option>
          </Select>
        </Form.Item>
        <Form.Item label="Fotoğraf Yükle">
          <Upload fileList={fileList} onChange={handleUpload} beforeUpload={() => false}>
            <Button>Fotoğraf Seç</Button>
          </Upload>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Kaydet
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default PropertyInputPage;
