import React, { useState, useEffect } from 'react';
import { Carousel, Card, Button } from 'antd';
import { useNavigate } from 'react-router-dom';

const Welcome = () => {
  const navigate = useNavigate();

  const images = [
    { id: 1, text: 'Hoşgeldiniz', image: 'url_of_first_image' },
    { id: 2, text: 'Ev ekleyebilirsiniz', image: 'url_of_second_image' },
    { id: 3, text: 'Evleri düzenleyebilirsiniz', image: 'url_of_third_image' },
  ];

  return (
    <div style={{ textAlign: 'center', padding: '50px' }}>
      <Carousel autoplay autoplaySpeed={7000}>
        {images.map((img) => (
          <div key={img.id}>
            <div style={{
              height: '300px',
              backgroundImage: img.image,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              color: 'white',
              fontSize: '24px'
            }}>
              {img.text}
            </div>
          </div>
        ))}
      </Carousel>
      <div style={{ marginTop: '20px' }}>
        <Card title="İşlemler" style={{ width: 300, margin: 'auto' }}>
          <Button type="primary" onClick={() => navigate('/property-input')} style={{ marginBottom: '10px' }}>
            Ev Ekle
          </Button>
          <Button type="primary" onClick={() => navigate('/property-edit')}>
            Düzenle
          </Button>
        </Card>
      </div>
    </div>
  );
};

export default Welcome;
