import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from './firebase';

const Login = ({ setIsLoggedIn }) => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onFinish = async (values) => {
    const { email, password } = values;
    setLoading(true);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setIsLoggedIn(true);
      message.success('Giriş başarılı!');
      navigate('/welcome'); // Girişten sonra welcome sayfasına yönlendir
    } catch (error) {
      message.error('Giriş başarısız, lütfen bilgilerinizi kontrol edin.');
      console.error('Giriş hatası:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Form
      name="login"
      onFinish={onFinish}
      style={{ maxWidth: '300px', margin: 'auto', paddingTop: '50px' }}
    >
      <Form.Item
        name="email"
        rules={[{ required: true, message: 'Lütfen e-posta adresinizi girin!' }]}
      >
        <Input placeholder="E-posta" />
      </Form.Item>
      <Form.Item
        name="password"
        rules={[{ required: true, message: 'Lütfen şifrenizi girin!' }]}
      >
        <Input.Password placeholder="Şifre" />
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit" loading={loading} style={{ width: '100%' }}>
          Giriş Yap
        </Button>
      </Form.Item>
    </Form>
  );
};

export default Login;
