import React from 'react';
import { List, Card, Button, Row, Col } from 'antd';
import { useNavigate } from 'react-router-dom';

const PropertyEdit = ({ properties, onDeleteProperty, onEditProperty }) => {
  const navigate = useNavigate();

  return (
    <div style={{ padding: '50px' }}>
      <h2>Emlak Düzenle</h2>
      <Row gutter={[16, 16]} style={{ marginBottom: '20px' }}>
        <Col span={12}>
          <Button type="primary" block onClick={() => navigate('/property-input')}>
            Ev Ekle
          </Button>
        </Col>
        <Col span={12}>
          <Button type="primary" block onClick={() => navigate('/property-list')}>
            Düzenle
          </Button>
        </Col>
      </Row>
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={properties}
        renderItem={item => (
          <List.Item>
            <Card
              title={item.ilanbaslik}
              actions={[
                <Button type="link" onClick={() => onEditProperty(item)}>Güncelle</Button>,
                <Button type="link" danger onClick={() => onDeleteProperty(item.id)}>Sil</Button>,
                <Button type="link" onClick={() => navigate(`/property-detail/${item.id}`)}>Detay</Button>
              ]}
            >
              <p>{item.ilanaciklama}</p>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
};

export default PropertyEdit;
