import React, { useState, useEffect } from 'react';
import { Layout, Menu, Button, Row, Col, Input, Select, Form, message, Card } from 'antd';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import PropertyInputPage from './PropertyInputPage';
import PropertyList from './PropertyList';
import PropertyDetail from './PropertyDetail';
import Favorites from './Favorites';
import Login from './Login';
import Logout from './Logout';
import { Element } from 'react-scroll';

const { Header, Content, Footer } = Layout;
const { Option } = Select;

const HomePage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [properties, setProperties] = useState([]);
  const [currentProperty, setCurrentProperty] = useState(null);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const storedProperties = JSON.parse(localStorage.getItem('properties')) || [];
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setProperties(storedProperties);
    setFavorites(storedFavorites);
  }, []);

  useEffect(() => {
    localStorage.setItem('properties', JSON.stringify(properties));
  }, [properties]);

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const handleNewProperty = (property) => {
    setProperties((prevProperties) => [...prevProperties, property]);
    setCurrentProperty(null); 
  };

  const handleDeleteProperty = (id) => {
    setProperties((prevProperties) => prevProperties.filter(property => property.id !== id));
  };

  const handleEditProperty = (property) => {
    setCurrentProperty(property);
  };

  const handleUpdateProperty = (updatedProperty) => {
    setProperties((prevProperties) => 
      prevProperties.map(property => property.id === updatedProperty.id ? updatedProperty : property)
    );
    setCurrentProperty(null); 
  };

  const handleToggleFavorite = (property) => {
    if (favorites.some(fav => fav.id === property.id)) {
      setFavorites((prevFavorites) => prevFavorites.filter(fav => fav.id !== property.id));
    } else {
      setFavorites((prevFavorites) => [...prevFavorites, property]);
    }
  };

  const handleContactSubmit = (values) => {
    message.success('Mesajınız gönderildi!');
  };

  return (
    <Router>
      <Layout className="layout">
        <Header style={{ backgroundColor: 'rgba(0, 0, 0, 0.8)', position: 'fixed', width: '100%', zIndex: 1 }}>
          <div className="logo" style={{ float: 'left', color: 'white', fontSize: '24px' }}>
            <Link to="/" style={{ color: 'white' }}>Dream Home Finder</Link>
          </div>
          <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']} style={{ backgroundColor: 'transparent', display: 'flex', justifyContent: 'flex-end' }}>
            <Menu.Item key="1">
              <Link to="/" style={{ color: 'white' }}>Ana Sayfa</Link>
            </Menu.Item>
            <Menu.Item key="2">
              <Link to="/property-list" style={{ color: 'white' }}>Emlak Listesi</Link>
            </Menu.Item>
            <Menu.Item key="3">
              <Link to="/favorites" style={{ color: 'white' }}>Favoriler</Link>
            </Menu.Item>
            {isLoggedIn && (
              <Menu.Item key="4">
                <Link to="/property-input" style={{ color: 'white' }}>Emlak Girişi</Link>
              </Menu.Item>
            )}
            <Menu.Item key="5">
              <Link to="/login" style={{ color: 'white' }}>{isLoggedIn ? 'Çıkış Yap' : 'Giriş Yap'}</Link>
            </Menu.Item>
            <Menu.Item key="6">
              <a href="#contact" style={{ color: 'white' }}>İletişim</a>
            </Menu.Item>
          </Menu>
        </Header>
        <Content style={{ padding: '0', minHeight: '100vh', overflow: 'hidden', marginTop: '64px' }}>
          <div style={{ 
            backgroundImage: 'url(https://izcelikyapi.com/wp-content/uploads/2022/05/iki-katli-celik-ev-modelleri.webp)', 
            backgroundSize: 'cover', 
            backgroundPosition: 'center', 
            height: '100vh', 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center',
            color: 'white',
            position: 'relative'
          }}>
            <div style={{ textAlign: 'center' }}>
              <h1 style={{ fontSize: '48px', fontWeight: 'bold' }}>Find Your New Home!</h1>
              <Row gutter={16} justify="center" style={{ marginTop: 20 }}>
                <Col>
                  <Input placeholder="Min Price" style={{ width: 120 }} />
                </Col>
                <Col>
                  <Input placeholder="Max Price" style={{ width: 120 }} />
                </Col>
                <Col>
                  <Input placeholder="Beds" style={{ width: 120 }} />
                </Col>
                <Col>
                  <Input placeholder="Baths" style={{ width: 120 }} />
                </Col>
                <Col>
                  <Select placeholder="Property Type" style={{ width: 150 }}>
                    <Option value="sale">For Sale</Option>
                    <Option value="rent">For Rent</Option>
                  </Select>
                </Col>
              </Row>
            </div>
          </div>
          <div style={{ padding: '50px', backgroundColor: '#f0f2f5' }}>
            <h2>Emlak Listesi</h2>
            <Row gutter={16}>
              {properties.map(property => (
                <Col key={property.id} span={8} style={{ marginBottom: '16px' }}>
                  <Card
                    hoverable
                    cover={<img alt={property.ilanbaslik} src={property.photos[0].url} />}
                  >
                    <Card.Meta title={property.ilanbaslik} description={`${property.fiyat} ${property.aciklama}`} />
                    <Link to={`/property-detail/${property.id}`}>
                      <Button type="primary" style={{ marginTop: '10px' }}>Detayları Gör</Button>
                    </Link>
                  </Card>
                </Col>
              ))}
            </Row>
          </div>
          <Element name="contact">
            <div id="contact" style={{ background: 'white', padding: '50px' }}>
              <h2>İletişim</h2>
              <Form onFinish={handleContactSubmit} layout="vertical">
                <Form.Item name="name" label="İsim" rules={[{ required: true, message: 'Lütfen isminizi girin' }]}>
                  <Input />
                </Form.Item>
                <Form.Item name="email" label="Email" rules={[{ required: true, type: 'email', message: 'Lütfen geçerli bir email adresi girin' }]}>
                  <Input />
                </Form.Item>
                <Form.Item name="message" label="Mesaj" rules={[{ required: true, message: 'Lütfen mesajınızı girin' }]}>
                  <Input.TextArea rows={4} />
                </Form.Item>
                <Form.Item>
                  <Button type="primary" htmlType="submit">Gönder</Button>
                </Form.Item>
              </Form>
            </div>
          </Element>
        </Content>
        <Footer style={{ textAlign: 'center', background: 'rgba(0, 0, 0, 0.7)', color: 'white' }}>
          Emlakçı Otomasyonu ©2024 Created by You
        </Footer>
      </Layout>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/logout" element={<Logout setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/property-input" element={
          <PropertyInputPage 
            onNewProperty={handleNewProperty}
            onUpdateProperty={handleUpdateProperty}
            currentProperty={currentProperty} 
          />
        } />
        <Route path="/property-list" element={
          <PropertyList 
            properties={properties} 
            onDeleteProperty={handleDeleteProperty}
            onEditProperty={handleEditProperty}
            onToggleFavorite={handleToggleFavorite}
            favorites={favorites}
          />
        } />
        <Route path="/property-detail/:id" element={<PropertyDetail properties={properties} />} />
        <Route path="/favorites" element={
          <Favorites 
            favorites={favorites} 
            onToggleFavorite={handleToggleFavorite} 
          />
        } />
      </Routes>
    </Router>
  );
};

export default HomePage;
