import React from 'react';
import { Table, Button, Space, Card, Row, Col } from 'antd';
import { Link } from 'react-router-dom';
import { StarOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';

const PropertyList = ({ properties, onDeleteProperty, onEditProperty, onToggleFavorite, favorites, isLoggedIn }) => {
  const columns = [
    {
      title: 'İlan Başlığı',
      dataIndex: 'ilanbaslik',
      key: 'ilanbaslik',
      render: (text, record) => <Link to={`/property-detail/${record.id}`}>{text}</Link>
    },
    {
      title: 'Fiyat',
      dataIndex: 'fiyat',
      key: 'fiyat',
    },
    {
      title: 'İl',
      dataIndex: 'il',
      key: 'il',
    },
    {
      title: 'İlçe',
      dataIndex: 'ilce',
      key: 'ilce',
    },
    {
      title: 'Kategori',
      dataIndex: 'kategori',
      key: 'kategori',
    },
    {
      title: 'Tür',
      dataIndex: 'tur',
      key: 'tur',
    },
    {
      title: 'Oda Sayısı',
      dataIndex: 'oda_sayisi',
      key: 'oda_sayisi',
    },
    {
      title: 'Favori',
      key: 'favori',
      render: (text, record) => (
        <Button
          icon={<StarOutlined />}
          onClick={() => onToggleFavorite(record)}
          type={favorites.some(fav => fav.id === record.id) ? 'primary' : 'default'}
        />
      ),
    },
    isLoggedIn ? {
      title: 'İşlemler',
      key: 'islemler',
      render: (text, record) => (
        <Space size="middle">
          <Button icon={<EditOutlined />} onClick={() => onEditProperty(record)}>Düzenle</Button>
          <Button icon={<DeleteOutlined />} onClick={() => onDeleteProperty(record.id)}>Sil</Button>
        </Space>
      ),
    } : {}
  ];

  return (
    <div style={{ padding: '50px' }}>
      <Table columns={columns.filter(col => col.key !== 'islemler' || isLoggedIn)} dataSource={properties} rowKey="id" />
    </div>
  );
};

export default PropertyList;
