import React from 'react';
import { List, Card, Button } from 'antd';

const Favorites = ({ favorites, onToggleFavorite }) => {
  return (
    <div style={{ maxWidth: 800, margin: 'auto' }}>
      <h1>Favoriler</h1>
      <List
        grid={{ gutter: 16, column: 1 }}
        dataSource={favorites}
        renderItem={item => (
          <List.Item>
            <Card title={item.ilanbaslik}>
              <p>Açıklama: {item.ilanaciklama}</p>
              <p>Fiyat: {item.fiyat}</p>
              <p>İl: {item.il}</p>
              <p>İlçe: {item.ilce}</p>
              <p>Kategori: {item.kategori}</p>
              <p>Tür: {item.tur}</p>
              {item.photos && item.photos.length > 0 && (
                <img src={item.photos[0].url} alt="Property" style={{ width: '100%' }} />
              )}
              <Button onClick={() => onToggleFavorite(item)}>
                {favorites.some(fav => fav.id === item.id) ? 'Favorilerden Çıkar' : 'Favorilere Ekle'}
              </Button>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
};

export default Favorites;
