import React from 'react';
import { List, Card, Button } from 'antd';
import { Link } from 'react-router-dom';

const PropertyEdit = ({ properties, onDeleteProperty, onEditProperty }) => {
  return (
    <div style={{ padding: '50px' }}>
      <h2>Emlak Düzenle</h2>
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={properties}
        renderItem={item => (
          <List.Item>
            <Card
              title={item.ilanbaslik}
              actions={[
                <Button type="link" onClick={() => onEditProperty(item)}>Güncelle</Button>,
                <Button type="link" danger onClick={() => onDeleteProperty(item.id)}>Sil</Button>,
                <Link to={`/property-detail/${item.id}`}>
                  <Button type="link">Detay</Button>
                </Link>
              ]}
            >
              <p>{item.ilanaciklama}</p>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
};

export default PropertyEdit;
