import React, { useState, useEffect } from 'react';
import { List, Card, Button, Input, Select, Form, Carousel, Modal } from 'antd';

const { Option } = Select;

const PropertyList = ({ onDeleteProperty, onEditProperty }) => {
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [properties, setProperties] = useState([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [currentPhotos, setCurrentPhotos] = useState([]);

  useEffect(() => {
    const storedProperties = JSON.parse(localStorage.getItem('properties')) || [];
    setProperties(storedProperties);
    setFilteredProperties(storedProperties);
  }, []);

  const handleFilter = (values) => {
    const filtered = properties.filter(property => {
      return (
        (!values.name || property.ilanbaslik.includes(values.name)) &&
        (!values.minPrice || property.fiyat >= values.minPrice) &&
        (!values.maxPrice || property.fiyat <= values.maxPrice) &&
        (!values.city || property.il === values.city) &&
        (!values.district || property.ilce === values.district) &&
        (!values.category || property.kategori === values.category) &&
        (!values.type || property.tur === values.type)
      );
    });
    setFilteredProperties(filtered);
  };

  const handleDelete = (id) => {
    const updatedProperties = properties.filter(property => property.id !== id);
    setProperties(updatedProperties);
    setFilteredProperties(updatedProperties);
    localStorage.setItem('properties', JSON.stringify(updatedProperties));
    onDeleteProperty(id);
  };

  const showModal = (photos) => {
    setCurrentPhotos(photos);
    setIsModalVisible(true);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto' }}>
      <h1>Emlak Listesi</h1>
      <Form layout="inline" onFinish={handleFilter}>
        <Form.Item name="name" label="İlan Başlığı">
          <Input placeholder="İlan Başlığı" />
        </Form.Item>
        <Form.Item name="minPrice" label="Min Fiyat">
          <Input placeholder="Min Fiyat" type="number" />
        </Form.Item>
        <Form.Item name="maxPrice" label="Max Fiyat">
          <Input placeholder="Max Fiyat" type="number" />
        </Form.Item>
        <Form.Item name="city" label="İl">
          <Select placeholder="İl seçin">
            <Option value="İstanbul">İstanbul</Option>
            <Option value="Ankara">Ankara</Option>
            <Option value="İzmir">İzmir</Option>
            {/* Diğer şehirleri ekleyin */}
          </Select>
        </Form.Item>
        <Form.Item name="district" label="İlçe">
          <Input placeholder="İlçe" />
        </Form.Item>
        <Form.Item name="category" label="Kategori">
          <Select placeholder="Kategori seçin">
            <Option value="konut">Konut</Option>
            <Option value="ticari">Ticari</Option>
            <Option value="arsa">Arsa</Option>
            <Option value="diğer">Diğer</Option>
          </Select>
        </Form.Item>
        <Form.Item name="type" label="Tür">
          <Select placeholder="Tür seçin">
            <Option value="kiralik">Kiralık</Option>
            <Option value="satilik">Satılık</Option>
          </Select>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">Filtrele</Button>
        </Form.Item>
      </Form>
      <List
        grid={{ gutter: 16, column: 1 }}
        dataSource={filteredProperties}
        renderItem={item => (
          <List.Item>
            <Card title={item.ilanbaslik}>
              <p>Açıklama: {item.ilanaciklama}</p>
              <p>Fiyat: {item.fiyat}</p>
              <p>İl: {item.il}</p>
              <p>İlçe: {item.ilce}</p>
              <p>Kategori: {item.kategori}</p>
              <p>Tür: {item.tur}</p>
              {item.photos && item.photos.length > 0 && (
                <div onClick={() => showModal(item.photos)}>
                  <img src={item.photos[0].url} alt="Property" style={{ width: '100%', cursor: 'pointer' }} />
                </div>
              )}
              <Button onClick={() => onEditProperty(item)}>Güncelle</Button>
              <Button danger onClick={() => handleDelete(item.id)}>Sil</Button>
            </Card>
          </List.Item>
        )}
      />
      <Modal visible={isModalVisible} footer={null} onCancel={handleCancel}>
        <Carousel arrows infinite={false}>
          {currentPhotos.map(photo => (
            <div key={photo.uid}>
              <img src={photo.url} alt="Property" style={{ width: '100%' }} />
            </div>
          ))}
        </Carousel>
      </Modal>
    </div>
  );
};

export default PropertyList;
