// src/pages/Login.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase';
import { Form, Input, Button, message } from 'antd';

const Login = () => {
  const navigate = useNavigate();

  const handleSubmit = async (values) => {
    try {
      await signInWithEmailAndPassword(auth, values.email, values.password);
      message.success('Başarıyla giriş yapıldı.');
      navigate('/property-list');
    } catch (error) {
      console.error("Giriş yapılırken hata oluştu:", error);
      message.error('Giriş yapılırken hata oluştu.');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto', padding: '50px' }}>
      <h2>Giriş Yap</h2>
      <Form onFinish={handleSubmit}>
        <Form.Item name="email" rules={[{ required: true, message: 'Lütfen emailinizi girin!' }]}>
          <Input placeholder="Email" />
        </Form.Item>
        <Form.Item name="password" rules={[{ required: true, message: 'Lütfen şifrenizi girin!' }]}>
          <Input.Password placeholder="Şifre" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">Giriş Yap</Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default Login;
