import React from 'react';
import { Carousel, Button, Card, Row, Col } from 'antd';
import { EditOutlined, DeleteOutlined, StarOutlined } from '@ant-design/icons';

const PropertyList = ({ properties, onDeleteProperty, onEditProperty, onToggleFavorite, favorites, isLoggedIn }) => {
  return (
    <div style={{ padding: '50px' }}>
      <Row gutter={[16, 16]}>
        {properties.map((property) => (
          <Col span={8} key={property.id}>
            <Card
              cover={
                <Carousel arrows infinite={false}>
                  {property.photos.map((photo, index) => (
                    <div key={index}>
                      <img src={photo.url} alt={property.ilanbaslik} style={{ height: '200px', objectFit: 'cover', width: '100%' }} />
                    </div>
                  ))}
                </Carousel>
              }
              actions={[
                isLoggedIn ? (
                  <>
                    <Button icon={<EditOutlined />} onClick={() => onEditProperty(property)} />
                    <Button icon={<DeleteOutlined />} onClick={() => onDeleteProperty(property.id)} />
                  </>
                ) : (
                  <Button icon={<StarOutlined />} onClick={() => onToggleFavorite(property)} />
                )
              ]}
            >
              <Card.Meta
                title={property.ilanbaslik}
                description={`${property.fiyat} ${property.il} ${property.ilce}`}
              />
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default PropertyList;
