import React, { useState, useEffect } from 'react';
import { List, Card, Button, Carousel, message } from 'antd';
import { StarOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';

const PropertyList = ({ onToggleFavorite, isLoggedIn }) => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    const storedProperties = JSON.parse(localStorage.getItem('properties')) || [];
    if (storedProperties.length > 0) {
      setProperties(storedProperties);
    } else {
      message.warning('Hiç emlak bulunamadı.');
    }
  }, []);

  return (
    <div style={{ padding: '50px' }}>
      <h2>Emlak Listesi</h2>
      {properties.length === 0 ? (
        <p>Henüz emlak eklenmemiş.</p>
      ) : (
        <List
          grid={{ gutter: 16, column: 3 }}
          dataSource={properties}
          renderItem={item => (
            <List.Item>
              <Card
                cover={
                  <Carousel arrows infinite={false}>
                    {item.photos.map((photo, index) => (
                      <div key={index}>
                        <img src={photo.url} alt={item.ilanbaslik} style={{ height: '200px', objectFit: 'cover', width: '100%' }} />
                      </div>
                    ))}
                  </Carousel>
                }
                title={item.ilanbaslik}
                actions={[
                  <Button type="link" onClick={() => onToggleFavorite(item)} icon={<StarOutlined />} />,
                  <Link to={`/property-detail/${item.id}`}>Detay</Link>
                ]}
              >
                <p>{item.ilanaciklama}</p>
              </Card>
            </List.Item>
          )}
        />
      )}
    </div>
  );
};

export default PropertyList;
