import React from 'react';
import { List, Card, Button } from 'antd';
import { StarOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import { useAuth } from './useAuth';

const PropertyList = ({ properties, onDeleteProperty, onEditProperty, onToggleFavorite, favorites }) => {
  const { isLoggedIn } = useAuth();

  return (
    <div style={{ padding: '50px' }}>
      <h2>Emlak Listesi</h2>
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={properties}
        renderItem={item => (
          <List.Item>
            <Card
              title={item.ilanbaslik}
              actions={[
                <Button type="link" onClick={() => onToggleFavorite(item)} icon={<StarOutlined />} />,
                isLoggedIn && <Button type="link" onClick={() => onEditProperty(item)}>GÃ¼ncelle</Button>,
                isLoggedIn && <Button type="link" danger onClick={() => onDeleteProperty(item.id)}>Sil</Button>,
                <Link to={`/property-detail/${item.id}`}>Detay</Link>
              ]}
            >
              <p>{item.ilanaciklama}</p>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
};

export default PropertyList;
