import React from 'react';
import { List, Card, Button } from 'antd';
import { StarOutlined, EditOutlined, DeleteOutlined, InfoCircleOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';

const PropertyList = ({ properties, onDeleteProperty, onEditProperty, onToggleFavorite, isLoggedIn }) => {
  return (
    <div style={{ padding: '50px' }}>
      <h2>Emlak Listesi</h2>
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={properties}
        renderItem={item => (
          <List.Item>
            <Card
              title={item.ilanbaslik}
              actions={[
                isLoggedIn ? (
                  <>
                    <Button type="link" onClick={() => onEditProperty(item)} icon={<EditOutlined />} />
                    <Button type="link" danger onClick={() => onDeleteProperty(item.id)} icon={<DeleteOutlined />} />
                  </>
                ) : (
                  <Button type="link" onClick={() => onToggleFavorite(item)} icon={<StarOutlined />} />
                ),
                <Link to={`/property-detail/${item.id}`}><Button type="link" icon={<InfoCircleOutlined />} /></Link>
              ]}
            >
              <p>{item.ilanaciklama}</p>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
};

export default PropertyList;
