import React, { useEffect, useState } from 'react';
import {  Card, Button, Carousel, Row, Col } from 'antd';
import { StarOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import propertiesData from './properties.json';

const PropertyList = ({ onDeleteProperty, onEditProperty, onToggleFavorite, isLoggedIn }) => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    // JSON dosyasındaki verileri yükleyin ve state'e ekleyin
    setProperties(propertiesData);
  }, []);

  return (
    <div style={{ padding: '50px' }}>
      <h2>Emlak Listesi</h2>
      <Row gutter={[16, 16]}>
        {properties.map(item => (
          <Col key={item.id} span={8}>
            <Card
              cover={
                <Carousel arrows infinite={false}>
                  {item.photos.map((photo, index) => (
                    <div key={index}>
                      <img src={photo.url} alt={item.ilanbaslik} style={{ height: '200px', objectFit: 'cover', width: '100%' }} />
                    </div>
                  ))}
                </Carousel>
              }
              title={item.ilanbaslik}
              actions={[
                <Button type="link" onClick={() => onToggleFavorite(item)} icon={<StarOutlined />} />,
                <Link to={`/property-detail/${item.id}`}>Detay</Link>
              ]}
            >
              <p>{item.ilanaciklama}</p>
              <p><strong>Fiyat:</strong> {item.fiyat} TL</p>
              <p><strong>İl:</strong> {item.il}</p>
              <p><strong>İlçe:</strong> {item.ilce}</p>
              <p><strong>Kategori:</strong> {item.kategori}</p>
              <p><strong>Tür:</strong> {item.tur}</p>
              <p><strong>Oda Sayısı:</strong> {item.odaSayisi}</p>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default PropertyList;
