import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Select, Row, Col, Space } from 'antd';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const { Option } = Select;

const Home = ({ onFilter, properties }) => {
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [selectedCityId, setSelectedCityId] = useState(null);
  const [loadingDistricts, setLoadingDistricts] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await axios.get(
          'https://turkiyeapi.dev/api/v1/provinces'
        );
        setCities(response.data.data);
      } catch (error) {
        console.error('Ä°ller alÄ±nÄ±rken hata oluÅŸtu:', error);
      }
    };
    fetchCities();
  }, []);

  useEffect(() => {
    const fetchDistricts = async () => {
      if (!selectedCityId) return;
      setLoadingDistricts(true);
      try {
        const response = await axios.get(
          `https://turkiyeapi.dev/api/v1/provinces/districts/${selectedCityId}`
        );
        setDistricts(response.data.data);
      } catch (error) {
        console.error('Ä°lÃ§eler alÄ±nÄ±rken hata oluÅŸtu:', error);
      } finally {
        setLoadingDistricts(false);
      }
    };
    fetchDistricts();
  }, [selectedCityId]);

  const handleFilter = (values) => {
    const filtered = properties.filter(property => {
      return (
        (!values.name || property.ilanbaslik.includes(values.name)) &&
        (!values.minPrice || property.fiyat >= values.minPrice) &&
        (!values.maxPrice || property.fiyat <= values.maxPrice) &&
        (!values.city || property.il === values.city) &&
        (!values.district || property.ilce === values.district) &&
        (!values.category || property.kategori === values.category) &&
        (!values.type || property.tur === values.type) &&
        (!values.rooms || property.oda_sayisi === values.rooms)
      );
    });
    onFilter(filtered);
    navigate('/filtered-properties'); // Filtrelenen evlerin listeleneceÄŸi sayfaya yÃ¶nlendirme
  };

  const customOptions = [
    { label: 'China', value: 'china', emoji: 'ðŸ‡¨ðŸ‡³', desc: 'China (ä¸­å›½)' },
    { label: 'USA', value: 'usa', emoji: 'ðŸ‡ºðŸ‡¸', desc: 'USA (ç¾Žå›½)' },
    { label: 'Japan', value: 'japan', emoji: 'ðŸ‡¯ðŸ‡µ', desc: 'Japan (æ—¥æœ¬)' },
    { label: 'Korea', value: 'korea', emoji: 'ðŸ‡°ðŸ‡·', desc: 'Korea (éŸ©å›½)' },
  ];

  const handleChange = (value) => {
    console.log(`selected ${value}`);
  };

  return (
    <div style={{ 
      backgroundImage: 'url(https://izcelikyapi.com/wp-content/uploads/2022/05/iki-katli-celik-ev-modelleri.webp)', 
      backgroundSize: 'cover', 
      backgroundPosition: 'center', 
      height: '100vh', 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center',
      color: 'white',
      position: 'relative'
    }}>
      <div style={{ textAlign: 'center', width: '80%' }}>
        <h1 style={{ fontSize: '48px', fontWeight: 'bold' }}>Find Your New Home!</h1>
        <Form layout="vertical" onFinish={handleFilter} style={{ background: 'rgba(0, 0, 0, 0.5)', padding: '20px', borderRadius: '10px' }}>
          <Form.Item name="name">
            <Input placeholder="Ä°lan BaÅŸlÄ±ÄŸÄ±" />
          </Form.Item>
          <Row gutter={[16, 16]}>
            <Col span={6}>
              <Form.Item name="minPrice">
                <Input placeholder="Min Fiyat" type="number" />
              </Form.Item>
            </Col>
            <Col span={6}>
              <Form.Item name="maxPrice">
                <Input placeholder="Max Fiyat" type="number" />
              </Form.Item>
            </Col>
            <Col span={6}>
              <Form.Item name="city">
                <Select placeholder="Ä°l seÃ§in" onChange={setSelectedCityId} style={{ width: '100%' }}>
                  {cities.map(city => (
                    <Option key={city.id} value={city.id}>
                      {city.name}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={6}>
              <Form.Item name="district">
                <Select placeholder="Ä°lÃ§e seÃ§in" loading={loadingDistricts} disabled={!selectedCityId} style={{ width: '100%' }}>
                  {districts.map(district => (
                    <Option key={district.id} value={district.name}>
                      {district.name}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={[16, 16]}>
            <Col span={6}>
              <Form.Item name="category">
                <Select
                  mode="multiple"
                  style={{ width: '100%' }}
                  placeholder="Kategori seÃ§in"
                  onChange={handleChange}
                  options={customOptions}
                  optionRender={(option) => (
                    <Space>
                      <span role="img" aria-label={option.data.label}>
                        {option.data.emoji}
                      </span>
                      {option.data.desc}
                    </Space>
                  )}
                />
              </Form.Item>
            </Col>
            <Col span={6}>
              <Form.Item name="type">
                <Select
                  mode="multiple"
                  style={{ width: '100%' }}
                  placeholder="TÃ¼r seÃ§in"
                  onChange={handleChange}
                  options={customOptions}
                  optionRender={(option) => (
                    <Space>
                      <span role="img" aria-label={option.data.label}>
                        {option.data.emoji}
                      </span>
                      {option.data.desc}
                    </Space>
                  )}
                />
              </Form.Item>
            </Col>
            <Col span={6}>
              <Form.Item name="rooms">
                <Select
                  mode="multiple"
                  style={{ width: '100%' }}
                  placeholder="Oda SayÄ±sÄ±"
                  onChange={handleChange}
                  options={customOptions}
                  optionRender={(option) => (
                    <Space>
                      <span role="img" aria-label={option.data.label}>
                        {option.data.emoji}
                      </span>
                      {option.data.desc}
                    </Space>
                  )}
                />
              </Form.Item>
            </Col>
            <Col span={6}>
              <Form.Item>
                <Button type="primary" htmlType="submit" style={{ width: '100%' }}>Ara</Button>
              </Form.Item>
            </Col>
          </Row>
        </Form>
      </div>
    </div>
  );
};

export default Home;
