import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Upload, Select, message } from 'antd';
import axios from 'axios';
import { type } from '@testing-library/user-event/dist/type';
const { Option } = Select;


const PropertyInputPage = () => {
  const [fileList, setFileList] = useState([]);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [selectedCityId, setSelectedCityId] = useState(null);
  const [loadingDistricts, setLoadingDistricts] = useState(false);

    const [name, setname] = useState({ name: '', description: '', price: '',city:'',district:'' ,category:'',type:''});


  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await axios.get(
          'https://v1.nocodeapi.com/ebr123/google_sheets/MBXXeYjRrYchwfWk?tabId=ıller&filterValue=SEHİRLER'
        );
        setCities(response.data.data);
      } catch (error) {
        console.error('İller alınırken hata oluştu:', error);
      }
    };
    fetchCities();
  }, []);

  useEffect(() => {
    const fetchDistricts = async () => {
      if (!selectedCityId) return;
      setLoadingDistricts(true);
      try {
        const response = await axios.get(
          `https://v1.nocodeapi.com/ebr123/google_sheets/MBXXeYjRrYchwfWk?tabId=ılceler&filterValue=${selectedCityId}`
        );
        setDistricts(response.data.data);
      } catch (error) {
        console.error('İlçeler alınırken hata oluştu:', error);
      } finally {
        setLoadingDistricts(false);
      }
    };
    fetchDistricts();
  }, [selectedCityId]);


  const handleUpload = ({ fileList }) => {
    setFileList(fileList);
  };

  const handleCityChange = (value) => {
    setSelectedCityId(value);
  };

  const handleSubmit = async (values) => {
    try {
      const propertyData = {
        ilanbaslik: e.target.value ,
        ilanaciklama: e.target.value ,
        fiyat: e.target.value ,
        il: e.target.value ,
        ilce: e.target.value ,
        kategori: e.target.value ,
        tur: e.target.value 
      };

      console.log('Gönderilen veri:', propertyData); // Gönderilen veriyi kontrol edin

      const response = await axios.post(
        'https://v1.nocodeapi.com/ebr123/google_sheets/lqTMRrvPLJWVvnkU?tabId=ilan', 
        { data: propertyData }
      );

      if (response.status === 200) {
        message.success('Veri başarıyla gönderildi');
      } else {
        message.error('Veri gönderilirken bir hata oluştu');
      }

    } catch (error) {
      console.error('Veri gönderilirken hata oluştu:', error.response ? error.response.data : error.message);
      message.error('Veri gönderilirken bir hata oluştu');
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto' }}>
      <h1>Emlak Bilgilerini Girin</h1>
      <Form layout="vertical" onFinish={handleSubmit}>
        <Form.Item label="İlan Başlığı" name="name" rules={[{ required: true, message: 'Lütfen ilan başlığını girin.' }]}>
          <Input 
          value={name.name}
          onChange={(e) => setname({ name: e.target.value })}/>
        </Form.Item>
        <Form.Item label="İlan Açıklaması" name="description">
          <Input.TextArea 
          value={name.description}
          onChange={(e) => setname({ description: e.target.value })}/>
        </Form.Item>
        <Form.Item label="Fiyat" name="price" rules={[{ required: true, message: 'Lütfen fiyatı girin.' }]}>
          <Input 
          value={name.price}
          onChange={(e) => setname({ price: e.target.value })}/>
        </Form.Item>
        <Form.Item label="İl" name="city" rules={[{ required: true, message: 'Lütfen ili seçin.' }]}>
          <Select
            style={{ width: '100%' }}
            placeholder="İl seçin"
            onChange={handleCityChange}
          >
            {cities.map((city) => (
              <Option key={city.id} value={city.id}>
                {city.SEHİRLER}
              </Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item label="İlçe" name="district" rules={[{ required: true, message: 'Lütfen ilçeyi seçin.' }]}>
          <Select
            style={{ width: '100%' }}
            placeholder="İlçe seçin"
            loading={loadingDistricts}
            disabled={!selectedCityId}
          >
            {districts.map((district) => (
              <Option key={district.id} value={district.id}>
                {district.ılceler}
              </Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item label="Kategori" name="category" rules={[{ required: true, message: 'Lütfen kategoriyi seçin.' }]}>
          <Select>
            <Option value="konut">Konut</Option>
            <Option value="ticari">Ticari</Option>
            <Option value="arsa">Arsa</Option>
            <Option value="diğer">Diğer</Option>
            value={name.category}
          onChange={(e) => setname({ category: e.target.Option })}
          </Select>

        </Form.Item>
        <Form.Item label="Tür" name="type" rules={[{ required: true, message: 'Lütfen türü seçin.' }]}>
          <Select>
            <Option value="kiralik">Kiralık</Option>
            <Option value="satilik">Satılık</Option>
          </Select>
        </Form.Item>
        <Form.Item label="Fotoğraf Yükle">
          <Upload
            fileList={fileList}
            onChange={handleUpload}
            beforeUpload={() => false} // Dosya yüklemesini manuel yapacağız
          >
            <Button>Fotoğraf Seç</Button>
          </Upload>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Kaydet
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};


export default PropertyInputPage;
