import React, { useState } from 'react';
import { List, Card, Button, Input, Form, message } from 'antd';
import { StarOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import { sendEmail } from './emailService';

const Favorites = ({ favorites, onToggleFavorite }) => {
  const [email, setEmail] = useState('');
  const [sending, setSending] = useState(false);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const sendFavoritesByEmail = async () => {
    setSending(true);

    const favoriteProperties = favorites.map(fav => ({
      ilanbaslik: fav.ilanbaslik,
      ilanaciklama: fav.ilanaciklama,
      fiyat: fav.fiyat,
      il: fav.il,
      ilce: fav.ilce,
    }));

    try {
      await sendEmail(email, favoriteProperties);
      message.success('Favori evler başarıyla gönderildi.');
    } catch (error) {
      console.error('E-posta gönderim hatası:', error);
      message.error('E-posta gönderiminde bir hata oluştu.');
    } finally {
      setSending(false);
    }
  };

  return (
    <div style={{ padding: '50px' }}>
      <h2>Favoriler</h2>
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={favorites}
        renderItem={item => (
          <List.Item>
            <Card
              title={item.ilanbaslik}
              actions={[
                <Button type="link" onClick={() => onToggleFavorite(item)} icon={<StarOutlined />} />,
                <Link to={`/property-detail/${item.id}`}>Detay</Link>
              ]}
            >
              <p>{item.ilanaciklama}</p>
            </Card>
          </List.Item>
        )}
      />
      <div style={{ marginTop: '50px', textAlign: 'center' }}>
        <h3>Favori Evleri Mail Gönder</h3>
        <Form layout="inline" onFinish={sendFavoritesByEmail}>
          <Form.Item
            name="email"
            rules={[{ required: true, message: 'Lütfen geçerli bir email adresi giriniz.' }]}
          >
            <Input
              placeholder="Email Adresiniz"
              value={email}
              onChange={handleEmailChange}
              type="email"
            />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" loading={sending}>
              Gönder
            </Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
};

export default Favorites;
