import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Layout, Card } from 'antd';
import axios from 'axios';

const { Content } = Layout;

const PropertyDetail = ({ properties }) => {
  const { id } = useParams();
  const property = properties.find(p => p.id === parseInt(id));
  const [provinces, setProvinces] = useState([]);

  useEffect(() => {
    const fetchProvinces = async () => {
      try {
        const response = await axios.get('https://turkiyeapi.dev/api/v1/provinces');
        setProvinces(response.data.data);
      } catch (error) {
        console.error('İller alınırken hata oluştu:', error);
      }
    };
    fetchProvinces();
  }, []);

  const getProvinceName = (provinceId) => {
    const province = provinces.find(province => province.id === provinceId);
    return province ? province.name : 'İl bulunamadı';
  };

  if (!property) {
    return <div>Property not found</div>;
  }

  return (
    <Layout className="layout">
      <Content style={{ padding: '50px', minHeight: '80vh' }}>
        <Card
          hoverable
          cover={<img alt={property.ilanbaslik} src={property.photos[0]?.url || 'https://via.placeholder.com/150'} />}
        >
          <Card.Meta title={property.ilanbaslik} description={`${property.fiyat} ${property.aciklama}`} />
          <p><strong>İlan Başlığı:</strong> {property.ilanbaslik}</p>
          <p><strong>Açıklama:</strong> {property.ilanaciklama}</p>
          <p><strong>Fiyat:</strong> {property.fiyat}</p>
          <p><strong>İl:</strong> {getProvinceName(property.il)}</p>
          <p><strong>İlçe:</strong> {property.ilce}</p>
          <p><strong>Kategori:</strong> {property.kategori}</p>
          <p><strong>Tür:</strong> {property.tur}</p>
        </Card>
      </Content>
    </Layout>
  );
};

export default PropertyDetail;
